INSERT INTO `san_pham` (`ma_san_pham`, `ma_loai`, `ten_san_pham`, `ten_san_pham_url`, `noi_dung_tom_tat`, `noi_dung_chi_tiet`, `don_gia`, `don_gia_khuyen_mai`, `khuyen_mai`, `hinh`, `ngay_cap_nhat`, `dvt`) VALUES
(101, 1, 'Tom Ford Ultra Rich Lip Color  03', 'tom-ford-ultra-rich-lip-color-03', '	\r\nCam San Hô', '<p>&ndash; Nếu bạn đ&atilde; l&agrave; t&iacute;n đồ h&acirc;m mộ son m&ocirc;i th&igrave; b&aacute;n ẽ kh&ocirc;ng thể n&agrave;o bỏ qua son của&nbsp;<strong>Tom Ford</strong>. Thương hiệu&nbsp;đẳng cấp với chất son hơn hẳn c&aacute;c d&ograve;ng son kh&aacute;c.</p>\r\n\r\n<p>&ndash;&nbsp;Son&nbsp;<strong>Tom Ford&nbsp;</strong>cũng được c&aacute;c chuy&ecirc;n gia&nbsp;make up h&agrave;ng đầu đ&aacute;nh gi&aacute; cao về chất lượng son khi đ&aacute;nh.</p>\r\n\r\n<p>&ndash; Chất son mềm mượt, đầy đặn, kh&ocirc;ng kh&ocirc; m&ocirc;i, l&ecirc;n m&agrave;u si&ecirc;u chuẩn, đ&acirc;y cũng l&agrave; đặc điểm kh&aacute;c biệt của son&nbsp;<strong>Tom Ford</strong>&nbsp;so với c&aacute;c d&ograve;ng son kh&aacute;c hiện nay.</p>\r\n\r\n<p>Th&ocirc;ng tin sản phẩm</p>\r\n\r\n<ul>\r\n	<li>Thương hiệu:&nbsp;Tom Ford</li>\r\n	<li>Loại son: son l&igrave;</li>\r\n	<li>M&agrave;u son:&nbsp;03 &ndash; Le Mepris</li>\r\n	<li>Khối lượng: 3g</li>\r\n	<li>Xuất xứ:&nbsp;Mỹ</li>\r\n</ul>\r\n', 1650000, 1600000, '50000', '1513883869zxc.jpg', '2017-12-21', 'VND'),
(102, 1, 'Tom Ford Lip Color Sheer 07', 'tom-ford-lip-color-sheer-07', 'Hồng Baby', '<p><strong>Tom Ford</strong>&nbsp;l&agrave; thương hiệu thời trang, mỹ phẩm cao cấp danh gi&aacute; tr&ecirc;n thế giới đến từ Mỹ. Được th&agrave;nh lập bởi nh&agrave; thiết kế nổi tiếng&nbsp;<strong>Tom Ford</strong>&nbsp;(từng l&agrave; gi&aacute;m đốc s&aacute;ng tạo của Gucci) từ năm 2004, đến nay&nbsp;<strong>Tom Ford</strong>&nbsp;đ&atilde; l&agrave; thương hiệu được y&ecirc;u th&iacute;ch v&agrave; tin d&ugrave;ng bởi c&aacute;c t&iacute;n đồ thời trang tr&ecirc;n thế giới ngang ngửa với những thương hiệu c&oacute; h&agrave;ng trăm năm tuổi đời. Kh&ocirc;ng chỉ được biết đến từ những bộ sưu tập thời trang, k&iacute;nh m&aacute;t tinh tế đẳng cấp m&agrave; mảng nước hoa đặc biệt l&agrave; son m&ocirc;i của&nbsp;<strong>Tom Ford</strong>&nbsp;cũng rất chất lượng v&agrave; được ưa chuộng.</p>\r\n\r\n<p><strong>Tom Ford Lip Color Sheer 07 Paradiso&nbsp;</strong>nằm trong bộ sưu tập son&nbsp;<strong>Lip Color Shee</strong>r của&nbsp;<strong>Tom Ford&nbsp;</strong>&ndash; l&agrave; thỏi son ho&agrave;n hảo để&nbsp;bảo vệ, nu&ocirc;i dưỡng đ&ocirc;i m&ocirc;i căng mọng, mềm mại đầy sức sống. B&ecirc;n cạnh đ&oacute; mặc d&ugrave; l&agrave; son dưỡng nhưng m&agrave;u của&nbsp;<strong>Tom Ford Color Sheer</strong>&nbsp;vẫn ho&agrave;n hảo cho đ&ocirc;i m&ocirc;i sắc m&agrave;u tươi tắn, ch&acirc;n thực c&ugrave;ng thiết kế kh&aacute;c biệt giới hạn sẽ chiều l&ograve;ng cả những c&ocirc; n&agrave;ng kh&oacute; t&iacute;nh nhất.</p>\r\n\r\n<p><strong>Paradiso</strong>&nbsp;mang tr&ecirc;n m&igrave;nh t&ocirc;ng hồng pha ch&uacute;t nhũ v&agrave;ng&nbsp;nữ t&iacute;nh nhẹ nh&agrave;ng,&nbsp;m&agrave;u son n&agrave;y nằm trong top những m&agrave;u&nbsp;son b&aacute;n chạy nhất của thương hiệu&nbsp;<strong>Tom Ford</strong>.&nbsp;Đặc biệt sắc hồng của&nbsp;<strong>Paradiso&nbsp;</strong>rất dễ t&iacute;nh cho mọi m&agrave;u da, cho việc kết hợp trang phục ph&ugrave; hợp cho bạn sử dụng cả khi đi l&agrave;m, đi học hay đi chơi đem đến vẻ đẹp tự nhi&ecirc;n sang trọng v&agrave; qu&yacute; ph&aacute;i.</p>\r\n\r\n<p><strong>Tom Ford&nbsp;Lip Color Sheer 07 Paradiso</strong>&nbsp;kh&ocirc;ng c&oacute; ch&igrave;, l&ecirc;n m&agrave;u tự nhi&ecirc;n, kh&ocirc;ng g&acirc;y ra th&acirc;m m&ocirc;i v&agrave; kh&ocirc; nứt, cũng kh&ocirc;ng g&acirc;y b&oacute;ng, d&iacute;nh hay nặng m&ocirc;i như một số d&ograve;ng son dưỡng kh&aacute;c. Trong&nbsp;<strong>Tom Ford M&agrave;u 07 Color Paradiso</strong>&nbsp;c&oacute; th&agrave;nh phần vitamin E v&agrave; C chiết xuất từ tự nhi&ecirc;n bao gồm hạt Soja, Bơ Murumuru ở Brazil v&agrave; tinh dầu của loại hoa Chamomilla&hellip; Tạo th&agrave;nh một kết cấu si&ecirc;u mềm mịn, gi&uacute;p cung cấp độ ẩm nu&ocirc;i dưỡng, bảo vệ m&ocirc;i sau từ b&ecirc;n trong cho bạn l&agrave;n m&ocirc;i lu&ocirc;n căng mọng mềm mịn đầy quyến rũ.</p>\r\n\r\n<p><strong>Th&ocirc;ng tin sản phẩm:</strong></p>\r\n\r\n<ul>\r\n	<li>Thương hiệu:&nbsp;Tom Ford</li>\r\n	<li>Loại son: Son b&oacute;ng</li>\r\n	<li>M&agrave;u son:&nbsp;07 &ndash; Paradiso</li>\r\n	<li>Khối lượng: 3g</li>\r\n	<li>Xuất xứ:&nbsp;Mỹ</li>\r\n</ul>\r\n', 1650000, 1600000, '50000', '1513916350paradiso.jpg', '2017-12-22', 'vnd'),
(104, 2, 'Kem Lót Burberry Fresh Glow Luminous Fluid Base', 'kem-lot-burberry-fresh-glow-luminous-fluid-base', 'Danh mục: Primer - Kem lót Từ khóa: Burberry, Fresh Glow, kem lót, Luminous Fluid Base', '<h2>Kem L&oacute;t Burberry Fresh Glow Luminous Fluid Base</h2>\r\n\r\n<p><img src=\"https://nutycosmetics.vn/wp-content/uploads/2017/02/16649283_1473390779372995_574007830130065937_n.jpg\" style=\"height:576px; width:960px\" /></p>\r\n\r\n<p><img alt=\"\" src=\"https://www.facebook.com/images/emoji.php/v9/f9/1.5/16/1f3af.png\" style=\"height:16px; width:16px\" />?&nbsp;Kem l&oacute;t Burberry Fresh Glow Luminous Fluid Base l&agrave; loại kem l&oacute;t c&oacute; độ dưỡng ẩm cao, gi&uacute;p l&agrave;n da kh&ocirc;ng bị kh&ocirc; tr&oacute;c, duy tr&igrave; lớp nền bền l&acirc;u.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.facebook.com/images/emoji.php/v9/f9/1.5/16/1f3af.png\" style=\"height:16px; width:16px\" />?&nbsp;Th&agrave;nh phần của em ấy c&ograve;n được bổ sung bột ngọc trai kh&ocirc;ng chỉ dễ d&agrave;ng che dấu những khuyết điểm nhỏ tr&ecirc;n da m&agrave; c&ograve;n gi&uacute;p l&agrave;n da bắt s&aacute;ng nhẹ nh&agrave;ng, tự nhi&ecirc;n, mang lại lớp nền s&aacute;ng khỏe hơn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.facebook.com/images/emoji.php/v9/f9/1.5/16/1f3af.png\" style=\"height:16px; width:16px\" />?&nbsp;Kết cấu kem kh&ocirc;ng qu&aacute; đặc cũng kh&ocirc;ng qu&aacute; lỏng, cực k&igrave; dễ apply l&ecirc;n da khiến da đều m&agrave;u v&agrave; ẩm mịn.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.facebook.com/images/emoji.php/v9/f9/1.5/16/1f3af.png\" style=\"height:16px; width:16px\" />?&nbsp;V&igrave; kh&ocirc;ng c&oacute; khả năng kiềm dầu n&ecirc;n em n&agrave;y cực k&igrave; ph&ugrave; hợp với những n&agrave;ng da thường v&agrave; da kh&ocirc;.</p>\r\n\r\n<p><img alt=\"\" src=\"https://www.facebook.com/images/emoji.php/v9/f9/1.5/16/1f3af.png\" style=\"height:16px; width:16px\" />?&nbsp;Th&agrave;nh phần của em ấy c&ograve;n được bổ sung chất dưỡng ẩm nhẹ cho da n&ecirc;n c&aacute;c n&agrave;ng c&oacute; thể d&ugrave;ng th&ecirc;m kem dưỡng hoặc apply trực tiếp l&ecirc;n da m&agrave; vẫn mang lại lớp nền b&oacute;ng khỏe, tự nhi&ecirc;n.</p>\r\n', 1100000, 1000000, '100000', '1514048250fghgf.jpg', '2017-12-23', 'VND'),
(105, 2, 'Nước Hoa Nữ My Burberry Festive 2016 Limited Edition Eau De Parfum', 'nuoc-hoa-nu-my-burberry-festive-2016-limited-edition-eau-de-parfum', 'Danh mục: Nước hoa nữ Dung tích: 5ml Từ khóa: Burberry, My Burberry Festive 2016 Limited Edition Eau De Parfum, Nước Hoa Nữ', '<p><strong>Nước Hoa Nữ My Burberry Festive 2016 Limited Edition Eau De Parfum</strong></p>\r\n\r\n<p>Gi&aacute;ng Sinh l&agrave; một trong những m&ugrave;a lễ hội lớn nhất trong năm v&agrave; kh&ocirc;ng bỏ lỡ dịp n&agrave;y, nhiều h&atilde;ng mỹ phẩm đ&atilde; tung ra những phi&ecirc;n bản hạn chế số lượng d&agrave;nh ri&ecirc;ng cho m&ugrave;a n&agrave;y với sự thay đổi về mẫu m&atilde;, m&agrave;u sắc cũng như điểm nhấn đắt gi&aacute; cho từng sản phẩm. Trong đ&oacute;, kh&ocirc;ng thể kh&ocirc;ng nhắc đến lọ nước hoa My Burberry Festive 2016 n&agrave;y rồi phải kh&ocirc;ng n&egrave;!!!</p>\r\n\r\n<p><img alt=\"burberry\" src=\"https://nutycosmetics.vn/wp-content/uploads/2016/05/burberry.jpg\" style=\"height:639px; width:960px\" /></p>\r\n\r\n<p>&ldquo;N&agrave;ng hương&rdquo; My Burberry Festive 2016 t&aacute;i hiện viễn cảnh lộng lẫy của những ng&agrave;y nghĩ lễ với những vụn kim tuyến l&oacute;ng l&aacute;nh trong th&acirc;n chai.</p>\r\n\r\n<p>C&aacute;c c&ocirc; n&agrave;ng sẽ cảm nhận thấy l&agrave;n da m&igrave;nh như được t&ocirc; điểm th&ecirc;m một ch&uacute;t &aacute;nh s&aacute;ng ngay sau khi xịt hỗn hợp l&ecirc;n da. Ngo&agrave;i ra, phi&ecirc;n bản c&ograve;n được chăm ch&uacute;t th&ecirc;m một chiếc nơ nhỏ xinh m&agrave;u v&agrave;ng nhạt c&agrave;ng l&agrave;m r&otilde; hơn tinh thần của những ng&agrave;y lễ.</p>\r\n\r\n<p><img alt=\"burberry-1\" src=\"https://nutycosmetics.vn/wp-content/uploads/2016/05/burberry-1-1.jpg\" style=\"height:636px; width:960px\" /></p>\r\n\r\n<p>Mở đầu l&agrave; hương hoa đậu ngọt ng&agrave;o kết hợp với hương cam bergamot, tiếp đến l&agrave; phong lữ, hoa lan Nam Phi v&agrave; c&acirc;y mộc v&agrave;ng. Lớp hương cuối được tạo th&agrave;nh từ hoắc hương, hoa hồng damsk v&agrave; hoa hồng Centifolia mang đến một cảm nhận thảo mộc nhẹ nh&agrave;ng, tương phản với ch&uacute;t cay nồng v&agrave; vị đắng nhưng kh&ocirc;ng k&eacute;m phần ngọt ng&agrave;o, trầm ấm, s&acirc;u lắng v&agrave; b&iacute; ẩn.</p>\r\n\r\n<p>Vừa cổ điển, thanh lịch, sang trọng nhưng kh&ocirc;ng k&eacute;m phần hấp dẫn, My Burberry sẽ l&agrave; sự lựa chọn ho&agrave;n hảo cho dịp lễ cuối năm n&agrave;y đấy nh&eacute;!!</p>\r\n\r\n<p><img alt=\"burberry-2\" src=\"https://nutycosmetics.vn/wp-content/uploads/2016/05/burberry-2-2.jpg\" style=\"height:622px; width:960px\" /></p>\r\n\r\n<p><img alt=\"burberry-3\" src=\"https://nutycosmetics.vn/wp-content/uploads/2016/05/burberry-3.png\" style=\"height:678px; width:960px\" /></p>\r\n', 2600000, 2650000, '50000', '1514048549zxczxczxc.png', '2017-12-23', 'VND'),
(106, 2, 'Set 4 Son Burberry Festive Mini Lip Velvet Set 2016 Holiday Limited Edition', 'set-4-son-burberry-festive-mini-lip-velvet-set-2016-holiday-limited-edition', 'Danh mục: Trang điểm, Lips Makeup, Son High-end Từ khóa: Set 4 Son Burberry', '<h2>Set 4 Son Burberry Festive Mini Lip Velvet Set 2016 Holiday Limited Edition</h2>\r\n\r\n<p>Burberry Festive Mini Lip Velvet Set 2016 Holiday Limited Edition ra đời như một m&oacute;n qu&agrave; sang chảnh l&agrave;m &ldquo;chao đảo&rdquo; tr&aacute;i tim của mọi t&iacute;n đồ y&ecirc;u son. C&oacute; thể n&oacute;i, được n&acirc;ng niu thỏi son sang chảnh n&agrave;y tr&ecirc;n tay l&agrave; niềm mơ ước của mọi c&ocirc; g&aacute;i.</p>\r\n\r\n<p>Mặc d&ugrave; vẫn giữ nguy&ecirc;n thiết kế với vu&ocirc;ng vức, nhưng set son lần n&agrave;y lại khiến cộng đồng cuồng son rạo rực hơn bao giờ hết, với thiết kế vỏ son v&agrave;ng qu&yacute; ph&aacute;i c&ugrave;ng họa tiết caro bao tr&ugrave;m l&ecirc;n những thỏi son, khiến bộ sưu tập c&agrave;ng trở n&ecirc;n lung linh v&agrave; bắt mắt hơn.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ mang ngoại h&igrave;nh h&uacute;t mắt, Burberry Festive Mini Lip Velvet Set 2016 Holiday Limited Edition c&ograve;n được h&atilde;ng tập hợp v&agrave; &ldquo;nh&agrave;o nặn&rdquo; th&agrave;nh cả một bộ sưu tập mini, n&agrave;ng n&agrave;o ngại &ldquo;v&iacute; eo hẹp&rdquo; kh&ocirc;ng đủ sức rước fullsize th&igrave; set son n&agrave;y sẽ l&agrave;m c&aacute;c n&agrave;ng thỏa m&atilde;n cơn ghiền son si&ecirc;u sang n&agrave;y nha.</p>\r\n', 1450000, 1400000, '50000', '1514048643iop.jpg', '2017-12-23', 'VND'),
(107, 1, 'Tom Ford Lip Color 09 323232323 qweqwe qweqwe qewqweqwe', 'tom-ford-lip-color-09-323232323', 'Giới tính	\r\nNữ\r\n\r\nThương hiệu	Tom Ford\r\nXuất xứ	\r\nMỹ\r\n\r\nMàu Son	\r\nCam San Hô', '<p>&ndash; Nếu bạn đ&atilde; l&agrave; t&iacute;n đồ h&acirc;m mộ son m&ocirc;i th&igrave; b&aacute;n ẽ kh&ocirc;ng thể n&agrave;o bỏ qua son của&nbsp;<strong>Tom Ford</strong>. Thương hiệu&nbsp;đẳng cấp với chất son hơn hẳn c&aacute;c d&ograve;ng son kh&aacute;c.</p>\r\n\r\n<p>&ndash;&nbsp;Son&nbsp;<strong>Tom Ford&nbsp;</strong>cũng được c&aacute;c chuy&ecirc;n gia&nbsp;make up h&agrave;ng đầu đ&aacute;nh gi&aacute; cao về chất lượng son khi đ&aacute;nh.</p>\r\n\r\n<p>&ndash; Chất son mềm mượt, đầy đặn, kh&ocirc;ng kh&ocirc; m&ocirc;i, l&ecirc;n m&agrave;u si&ecirc;u chuẩn, đ&acirc;y cũng l&agrave; đặc điểm kh&aacute;c biệt của son&nbsp;<strong>Tom Ford</strong>&nbsp;so với c&aacute;c d&ograve;ng son kh&aacute;c hiện nay.</p>\r\n\r\n<p>Th&ocirc;ng tin sản phẩm</p>\r\n\r\n<ul>\r\n	<li>Thương hiệu:&nbsp;Tom Ford</li>\r\n	<li>Loại son: son l&igrave;</li>\r\n	<li>M&agrave;u son:&nbsp;09 &ndash; True Coral</li>\r\n	<li>Khối lượng: 3g</li>\r\n	<li>Xuất xứ:&nbsp;Mỹ</li>\r\n</ul>\r\n', 1550000, 1300000, '150000', '1514048751vbn.jpg', '2017-12-23', 'VND'),
(109, 4, 'Calvin Klein CK Eternity Intense For Women', 'calvin-klein-ck-eternity-intense-for-women', 'Dung tích	\r\n100 ml\r\n\r\nLoại nước hoa	\r\nNữ\r\n\r\nMùi Hương Chính	\r\nHoa Diên Vỹ ( Iris ), Hương Va-ni ( Vanilla ), Rễ cây diên vĩ (Iris Root)\r\n\r\nNhóm hương	\r\nFloral ( Hương Hoa, Phấn Thơm )\r\n\r\nNăm phát hành	\r\n2016\r\n\r\nNồng độ	\r\nEau de Parfum (EDP)\r\n\r\nPhong cách	\r\nLãng Mạn, Quyến Rũ, Sâu Lắng\r\n\r\nThương hiệu	Calvin Klein (CK)\r\nXuất xứ	\r\nMỹ', '<p><strong>Calvin Klein Eternity For Women</strong>&nbsp;(1988) v&agrave;&nbsp;<strong>Eternity For Men</strong>&nbsp;(1989) biểu tượng hương thơm của t&igrave;nh y&ecirc;u vĩnh cửu v&agrave; c&aacute;c mối quan hệ l&atilde;ng mạn sẽ được t&aacute;i hiện với cường độ cao hơn, s&acirc;u sắc hơn với&nbsp;<strong>Eternity Intense&nbsp;For Women&nbsp;</strong>v&agrave;<strong>&nbsp;Eternity Intense For Men</strong>&nbsp;ra mắt v&agrave;o th&aacute;ng 12/2016. Hai d&ograve;ng nước hoa mới được thực hiện với c&aacute;c note hương mới đầy gợi cảm v&agrave; độc nhất của hoa iris. Bộ đ&ocirc;i&nbsp;<strong>Eternity Intense</strong>&nbsp;đại diện cho &ldquo;t&igrave;nh y&ecirc;u đ&atilde; biến th&agrave;nh một thứ g&igrave; đ&oacute; mạnh mẽ hơn, một kết nối đam m&ecirc; v&agrave; kh&ocirc;ng thể ph&aacute; vỡ&rdquo;</p>\r\n\r\n<p><img alt=\"\" src=\"https://orchard.vn/wp-content/uploads/2017/10/calvin-klein-eternity-intense-profumi-1000-1.jpg\" style=\"height:530px; width:700px\" /><br />\r\n<strong>Eternity Intense</strong>&nbsp;d&agrave;nh cho nữ được c&ocirc;ng bố l&agrave; tăng cường nhiều th&agrave;nh phần hoa quả phong ph&uacute; của phi&ecirc;n bản gốc, với c&aacute;c note hương nước v&agrave; hoa bổ sung: hoa iris tạo n&ecirc;n sự sang trọng hơn tr&ecirc;n nền hương cuối của vanilla, gỗ đ&agrave;n hương gợi cảm mang lại cho bạn sự ấm c&uacute;ng v&agrave; chiều s&acirc;u. Hương thơm được s&aacute;ng tạo ng&agrave;y c&agrave;ng c&oacute; sức h&uacute;t tăng cường với thời gian. Tươi mới v&agrave; phức tạp, &aacute;nh s&aacute;ng v&agrave; b&oacute;ng tối, s&acirc;u sắc v&agrave; th&acirc;n mật.&nbsp;<strong>ETERNITY INTENSE</strong>&nbsp;ph&aacute;t triển với t&iacute;nh c&aacute;ch s&acirc;u sắc, quyến rũ, trở th&agrave;nh một li&ecirc;n kết cảm gi&aacute;c bất th&agrave;nh văn giữa c&aacute;c loại nước hoa của đ&agrave;n &ocirc;ng v&agrave; phụ nữ.</p>\r\n\r\n<p><img alt=\"\" src=\"https://orchard.vn/wp-content/uploads/2017/10/SAM_7025-kopia-1024x698.jpg\" style=\"height:477px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Th&ocirc;ng tin sản phẩm:</strong></p>\r\n\r\n<ul>\r\n	<li>Thương hiệu:&nbsp;Calvin Klein</li>\r\n	<li>Nồng độ: Eau De Parfum&nbsp;(EDP)</li>\r\n	<li>Loại nước hoa nữ</li>\r\n	<li>M&ugrave;i hương đặc trưng\r\n	<ul>\r\n		<li>Hương đầu: hương nước, chanh tươi, rễ di&ecirc;n vĩ</li>\r\n		<li>Hương giữa: hoa iris,&nbsp;hoa mộc t&ecirc;, hoa hồng</li>\r\n		<li>Hương cuối: vanilla, gỗ đ&agrave;n hương, xạ hương.</li>\r\n	</ul>\r\n	</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"https://orchard.vn/wp-content/uploads/2017/10/4.jpg\" style=\"height:312px; width:202px\" /></p>\r\n\r\n<ul>\r\n	<li>Nh&oacute;m hương: Floral</li>\r\n	<li>Phong c&aacute;ch: l&atilde;ng mạn, quyến rũ, s&acirc;u sắc</li>\r\n	<li>Dung t&iacute;ch: 100ml</li>\r\n	<li>C&aacute;ch sử dụng: Xịt</li>\r\n	<li>Năm ph&aacute;t h&agrave;nh: 2016</li>\r\n	<li>Xuất xứ: Mỹ</li>\r\n</ul>\r\n', 1600000, 1500000, '100000', '1514049126tyu.jpg', '2017-12-23', 'VND'),
(111, 4, 'Calvin Klein Eternity 25th Anniversary Edition For Women', 'calvin-klein-eternity-25th-anniversary-edition-for-women', 'Giới tính	\r\nNữ\r\n\r\nThương hiệu	Calvin Klein (CK)\r\nNồng độ	\r\nEau De Perfume (EDP)\r\n\r\nLoại nước hoa	\r\nNữ\r\n\r\nDung tích	\r\n100\r\n\r\nNhóm hương	\r\nFloral ( Hương Hoa, Phấn Thơm )\r\n\r\nPhong cách	\r\nGợi Cảm, Hiện đại, Trẻ Trung\r\n\r\nNăm phát hành	\r\n2014\r\n\r\nXuất xứ	\r\nMỹ', '<p>Năm 2014, nh&acirc;n dịp kỉ niệm 25 năm thương hiệu Calvin Klein ra đời, h&atilde;ng nước hoa nổi tiếng của Mỹ đ&atilde; cho ra mắt d&ograve;ng sản phẩm kỉ niệm mang t&ecirc;n&nbsp;<strong>CK Eternity 25th Anniversary Edition For Women</strong>&nbsp;v&agrave;&nbsp;<strong>CK Eternity 25th Anniversary Edition For Men</strong>.&nbsp;</p>\r\n\r\n<p>Phi&ecirc;n bản d&agrave;nh cho nữ n&agrave;y mang hương thơm dịu nhẹ, tinh tế v&agrave; gợi cảm. Lớp hương đầu được tạo&nbsp;n&ecirc;n bởi lan Nam Phi, qu&yacute;t v&agrave; c&acirc;y x&ocirc; thơm. Quyện v&agrave;o đ&oacute; l&agrave; hương giữa của hoa linh lan, hoa huệ t&acirc;y, c&uacute;c vạn thọ v&agrave; thủy ti&ecirc;n v&agrave;ng. Cuối c&ugrave;ng l&agrave; n&eacute;t mềm mại, gợi cảm từ&nbsp;gỗ đ&agrave;n hương, hổ ph&aacute;ch, xạ hương.</p>\r\n\r\n<p>Th&ocirc;ng tin sản phẩm</p>\r\n\r\n<ul>\r\n	<li>Thương hiệu: Calvin Klein</li>\r\n	<li>Nồng độ: Eau De Parfum&nbsp;(EDP)</li>\r\n	<li>Loại nước hoa nữ</li>\r\n	<li>M&ugrave;i hương đặc trưng\r\n	<ul>\r\n		<li>Hương đầu: Lan Nam Phi, qu&yacute;t, c&acirc;y x&ocirc; thơm</li>\r\n		<li>Hương giữa: linh lan, huệ t&acirc;y, c&uacute;c vạn thọ, thủy ti&ecirc;n v&agrave;ng</li>\r\n		<li>Hương cuối: đ&agrave;n hương, hổ ph&aacute;ch, xạ hương</li>\r\n	</ul>\r\n	</li>\r\n	<li>Nh&oacute;m hương: Floral</li>\r\n	<li>Phong c&aacute;ch: trẻ trung, gợi cảm, hiện đại</li>\r\n	<li>Dung t&iacute;ch: 100ml</li>\r\n	<li>C&aacute;ch sử dụng: Xịt</li>\r\n	<li>Năm ph&aacute;t h&agrave;nh: 2014</li>\r\n	<li>Xuất xứ: Mỹ<strong>&nbsp;</strong></li>\r\n</ul>\r\n', 1880000, 1800000, '80000', '1514049390ert.jpg', '2017-12-23', 'VND'),
(112, 5, 'Gift Set Christian Rouge Louboutin', 'gift-set-christian-rouge-louboutin', 'Đẳng Cấp Sang Trọng Bậc Nhất - Gift Set Christian Rouge Louboutin', '<p><strong><strong>Đẳng Cấp Sang Trọng Bậc Nhất - Gift Set Christian Rouge Louboutin</strong></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"http://file.hstatic.net/1000025647/file/christian-louboutin-rouge-louboutin-gift-set_8119421f588f4c03acb6f301abfdc763_1024x1024.png\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img src=\"http://file.hstatic.net/1000025647/file/screenshot__1__325f24f3d455479093100c650820e2d8_1024x1024.png\" /></p>\r\n\r\n<p><img src=\"http://file.hstatic.net/1000025647/file/christian-louboutin-gift_1024x1024.png\" /></p>\r\n\r\n<p><strong><a href=\"http://thegioisonmoi.com/products/gift-set-christian-rouge-louboutin-001s-001-001m\">Gift Rouge Louboutin&nbsp;</a>n&agrave;y gồm 3 d&ograve;ng son trong phi&ecirc;n bản đặc biệt của Louboutin l&agrave;:</strong></p>\r\n\r\n<p><strong>+ Rouge Louboutin Silky Satin Lip Colour:</strong>&nbsp;D&ograve;ng son b&aacute;n l&igrave; kết hợp giữa son l&igrave; v&agrave; son dưỡng</p>\r\n\r\n<p><img src=\"http://file.hstatic.net/1000025647/file/001_b87a50bf14ec412095296952f69b2200_1024x1024.jpg\" /></p>\r\n\r\n<p><strong>+ Rouge Louboutin Velvet Matte Lip Colour</strong>: D&ograve;ng son c&oacute; độ giữ m&agrave;u v&agrave; độ phủ tốt.</p>\r\n\r\n<p><img src=\"http://file.hstatic.net/1000025647/file/001m_grande.jpeg\" /></p>\r\n\r\n<p><strong>+ Rouge Louboutin Sheer Voile Lip Colour</strong>: D&ograve;ng son c&oacute; nhiều dưỡng, gi&uacute;p cho đ&ocirc;i m&ocirc;i mềm mượt, kh&ocirc;ng kh&ocirc; m&ocirc;i.</p>\r\n\r\n<p><img src=\"http://file.hstatic.net/1000025647/file/001s_23c68b992302498a958c13c425c05c96_grande.jpg\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>- Thỏi&nbsp;<em><em><strong><strong><a href=\"http://thegioisonmoi.com/collections/son-louboutin\">Son Louboutin</a></strong></strong></em></em>&nbsp;c&oacute; thiết kế độc đ&aacute;o lấy cảm hứng từ Nữ ho&agrave;ng Ai Cập Nefertiti c&ugrave;ng bảng m&agrave;u đẹp kh&oacute; cưỡng.&nbsp;</p>\r\n\r\n<p>-&nbsp;<a href=\"http://thegioisonmoi.com/collections/son-louboutin\"><em><em><strong><strong>Son Christian Louboutin</strong></strong></em></em>&nbsp;</a>c&oacute; 3 d&ograve;ng son: Son &nbsp;l&igrave; (Velvet Matte), ẩm (Silky Stain) v&agrave; si&ecirc;u mượt (Sheer Voile) ph&ugrave; hợp với tất cả t&iacute;n đồ son m&ocirc;i.</p>\r\n\r\n<p>- M&agrave;u son l&ecirc;n đẹp, kh&ocirc;ng hề kh&ocirc;, dưỡng ẩm v&agrave; rất nhẹ. M&agrave;u son kh&aacute; trong v&agrave; mướt với chỉ một lớp duy nhất, kh&ocirc;ng d&iacute;nh bết khi b&ocirc;i. Son c&oacute; m&ugrave;i hương hoa nhẹ nh&agrave;ng rất dễ chịu.</p>\r\n\r\n<p>- Điểm nổi bật nhất l&agrave; vỏ son được trang tr&iacute; nghệ thuật tinh xảo vảy rồng.</p>\r\n\r\n<p>Quy c&aacute;ch:&nbsp;Thỏi 3.5g &amp; 3.8g</p>\r\n\r\n<p>Xuất xứ:&nbsp;Made in&nbsp;USA</p>\r\n\r\n<p>Tags:&nbsp;<em><em><strong><strong><a href=\"http://thegioisonmoi.com/collections/son-louboutin\">Son Christian Louboutin</a></strong></strong></em></em>,&nbsp;<strong><strong><em><em><a href=\"http://thegioisonmoi.com/collections/son-louboutin\">Son Louboutin</a></em></em></strong></strong></p>\r\n', 7500000, 7200000, '300000', '1514049535vbnvbnbb.png', '2017-12-23', 'VND'),
(113, 5, 'Son Christian Louboutin Màu 100 Bengali', 'son-christian-louboutin-mau-100-bengali', ' BẢO ĐẢM HÀNG CHẤT LƯỢNG CHÍNH HÃNG 100%\r\n DATE MỚI NHẤT\r\n GIAO HÀNG NHANH NHẤT\r\n PHỤC VỤ ĐẾN KHI KHÁCH HÀNG HÀI LÒNG\r\n 30 NGÀY BAO NGÀY ĐỔI TRẢ, HOÀN TIỀN 100% NẾU KHÔNG ĐÚNG CHẤT LƯỢNG.', '<p style=\"margin-left:0px; margin-right:0px; text-align:start\"><span style=\"font-size:12px\"><span style=\"color:#191a19\"><span style=\"background-color:#ffffff\"><span style=\"font-family:&quot;open sans&quot;\"><span style=\"font-size:12pt\"><span style=\"color:#666666 !important\"><strong><span style=\"color:#666666 !important\"><span style=\"font-family:&quot;Times New Roman&quot;\">Son Christian Louboutin M&agrave;u&nbsp;100 Bengali</span></span></strong></span></span></span></span></span></span><br />\r\n&nbsp;</p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#666666\"><span style=\"font-family:Tinos,Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:12pt\"><span style=\"color:#666666 !important\">- Thỏi&nbsp;<em><strong><a href=\"http://thegioisonmoi.com/collections/son-louboutin\" style=\"box-sizing:border-box; margin:0px; padding:0px; outline:none; color:#666666 !important; text-decoration:none; max-width:100%\" title=\"Son Louboutin \">Son Louboutin</a></strong></em>&nbsp;c&oacute; thiết kế độc đ&aacute;o lấy cảm hứng từ Nữ ho&agrave;ng Ai Cập Nefertiti c&ugrave;ng bảng m&agrave;u đẹp kh&oacute; cưỡng.&nbsp;</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#666666\"><span style=\"font-family:Tinos,Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:12pt\"><span style=\"color:#666666 !important\">-&nbsp;<a href=\"http://thegioisonmoi.com/collections/son-louboutin\" style=\"box-sizing:border-box; margin:0px; padding:0px; outline:none; color:#666666 !important; text-decoration:none; max-width:100%\" title=\"Son Christian Louboutin \"><em><strong>Son Christian Louboutin</strong></em>&nbsp;</a>c&oacute; 3 d&ograve;ng son: Son &nbsp;l&igrave; (Velvet Matte), ẩm (Silky Stain) v&agrave; si&ecirc;u mượt (Sheer Voile) ph&ugrave; hợp với tất cả t&iacute;n đồ son m&ocirc;i.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#666666\"><span style=\"font-family:Tinos,Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:12pt\"><span style=\"color:#666666 !important\">- M&agrave;u son l&ecirc;n đẹp, kh&ocirc;ng hề kh&ocirc;, dưỡng ẩm v&agrave; rất nhẹ. M&agrave;u son kh&aacute; trong v&agrave; mướt với chỉ một lớp duy nhất, kh&ocirc;ng d&iacute;nh bết khi b&ocirc;i. Son c&oacute; m&ugrave;i hương hoa nhẹ nh&agrave;ng rất dễ chịu.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#666666\"><span style=\"font-family:Tinos,Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:12pt\"><span style=\"color:#666666 !important\">- Điểm nổi bật nhất l&agrave; vỏ son được trang tr&iacute; nghệ thuật tinh xảo vảy rồng.</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#666666\"><span style=\"font-family:Tinos,Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:12pt\"><span style=\"color:#666666 !important\">Quy c&aacute;ch:&nbsp;Thỏi 3.5g &amp; 3.8g</span></span></span></span></span></span></p>\r\n\r\n<p style=\"text-align:start\"><span style=\"font-size:14px\"><span style=\"color:#666666\"><span style=\"font-family:Tinos,Arial,Helvetica,sans-serif\"><span style=\"background-color:#ffffff\"><span style=\"font-size:12pt\"><span style=\"color:#666666 !important\">Xuất xứ:&nbsp;Made in&nbsp;</span></span>USA</span></span></span></span></p>\r\n', 2950000, 2850000, '100000', '1514049613fghfhgfghfhfh.jpg', '2017-12-23', 'VND'),
(114, 7, 'Tresor In Love Eau de Parfum', 'tresor-in-love-eau-de-parfum', 'Nước Hoa Nữ Lancome Tresor In Love EDP 75ml', '<p><strong>Nước Hoa Nữ Lancome Tresor In Love EDP 75ml</strong></p>\r\n\r\n<p><strong>Lancome Paris l&agrave; thương hiệu mỹ phẩm nổi tiếng của Ph&aacute;p</strong>&nbsp;được th&agrave;nh lập v&agrave;o năm 1935 bởi Armand Petitjean &ndash; một cựu nh&acirc;n vi&ecirc;n của h&atilde;ng Coty. C&oacute; nguồn gốc từ c&ocirc;ng ty kinh doanh về nước hoa, Lancome đ&atilde; từng tung ra thị trường 5 loại nước hoa tại Hội chợ Thế giới năm 1935 ở Brussels, Bỉ. Sau đ&oacute;, c&ocirc;ng ty mở rộng quy m&ocirc; sang lĩnh vực mỹ phẩm v&agrave; những sản phẩm chăm s&oacute;c da cho cả phụ nữ v&agrave; nam giới.&nbsp;<strong>Ng&agrave;y nay, thương hiệu Lanc&ocirc;me trở n&ecirc;n rất nổi tiếng m&agrave; hầu như ai ai cũng biết đến, c&ocirc;ng ty lu&ocirc;n hợp t&aacute;c với những người nổi tiếng v&agrave; những nhiếp ảnh gia h&agrave;ng đầu tr&ecirc;n thế giới để tạo n&ecirc;n h&igrave;nh ảnh đẹp cho c&ocirc;ng ty.</strong>&nbsp;H&atilde;ng nước hoa Lanc&ocirc;me hiện đ&atilde; c&oacute; hơn 89 loại nước hoa.</p>\r\n\r\n<p>Th&aacute;ng 3 năm 2010, Lanc&ocirc;me cho ra mắt d&ograve;ng nước hoa&nbsp;<strong>Tr&eacute;sor&nbsp;In Love</strong>. Dưới sự s&aacute;ng tạo của Dominique v&agrave; Veronique Nyberg, sức l&ocirc;i cuốn của&nbsp;<strong>Tresor in Love ẩn chứa trong th&agrave;nh phần hương liệu với sự h&ograve;a quyện ho&agrave;n hảo của tr&aacute;i c&acirc;y v&agrave; hoa cỏ tr&ecirc;n nền hương gỗ tuyết t&ugrave;ng trầm lắng.</strong>&nbsp;<strong>Tr&eacute;sor&nbsp; In Love</strong>&nbsp;rất ph&ugrave; hợp với tiết trời m&aacute;t l&agrave;nh mơn mởn của m&ugrave;a xu&acirc;n, m&ugrave;a của t&igrave;nh y&ecirc;u mới chớm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Nước Hoa Nữ Lancome Tresor In Love EDP 75ml\" src=\"https://hasaki.vn/media/wysiwyg/tho/13939257916202.jpg\" style=\"height:442px; width:700px\" /></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Tresor In Love</strong>&nbsp;khơi dậy hương vị tr&aacute;i c&acirc;y căng mọng ngon ngọt của quả xu&acirc;n đ&agrave;o, đ&agrave;o v&agrave; quả l&ecirc; chua. Hương hoa hồng dịu &ecirc;m giữ cho chất vị tr&aacute;i c&acirc;y tươi m&aacute;t vẹn nguy&ecirc;n trong suốt thời gian nước hoa bay hơi. Một ch&uacute;t xạ hương l&agrave;m điểm nhấn bất ngờ dẫn dắt ta say sưa trong vị đ&agrave;o m&ecirc; hoặc.&nbsp;<strong>Tresor In Love mềm mại, nữ t&iacute;nh, dễ chịu m&agrave; kh&ocirc;ng qu&aacute; ngọt. Đ&acirc;y l&agrave; một m&ugrave;i hương trẻ trung đầy nữ t&iacute;nh, tạo cảm gi&aacute;c th&acirc;n thiện nhưng vẫn v&ocirc; c&ugrave;ng ki&ecirc;u sa cho ph&aacute;i đẹp.&nbsp;</strong>Thiết kế của d&ograve;ng nước hoa n&agrave;y gợi vẻ l&ocirc;i cuốn ngay từ c&aacute;i nh&igrave;n đầu ti&ecirc;n bởi sự chuyển m&agrave;u tinh tế từ gam m&agrave;u hồng phấn sang hồng nhạt. Dung dịch nước hoa được chứa đựng trong lọ thủy tinh cao, th&ecirc;m một b&ocirc;ng hồng đen điểm tr&ecirc;n cổ chai c&agrave;ng t&ocirc;n vinh vẻ đẹp thanh lịch v&agrave; sang trọng tuyệt vời.</p>\r\n\r\n<p><strong>Tresor In Love</strong>&nbsp;l&agrave; hương thơm ngọt ng&agrave;o, trẻ trung lấy cảm hứng từ phi&ecirc;n bản Tresor gốc, kế thừa được điểm tốt của Tresor gốc nhưng&nbsp;<strong>Tresor In Love</strong>&nbsp;được tăng th&ecirc;m độ nữ t&iacute;nh v&agrave; đ&aacute;ng y&ecirc;u trong m&ugrave;i hương.&nbsp;<strong>Tỏa s&aacute;ng, s&ocirc;i động v&agrave; gợi cảm, Tresor in Love tựa như một bản t&igrave;nh ca bay bổng, thơ mộng, l&agrave;m say l&ograve;ng người.</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong><img alt=\"Nước Hoa Nữ Lancome Tresor In Love EDP 75ml\" src=\"https://hasaki.vn/media/wysiwyg/tho/6e3f51397735ddd1362fc3b401a25c29-copyasa.jpg\" style=\"height:552px; width:736px\" /></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>M&ugrave;i hương đặc trưng:</strong></p>\r\n\r\n<p>Hương đầu: Cam Bergamot, Hồng ti&ecirc;u, Quả l&ecirc;, Tr&aacute;i xu&acirc;n đ&agrave;o.</p>\r\n\r\n<p>Hương giữa: Hoa nh&agrave;i, Hoa hồng Taif, Hoa t&iacute;m, Quả đ&agrave;o.</p>\r\n\r\n<p>Hương cuối: Xạ hương, Gỗ tuyết t&ugrave;ng Virginia.</p>\r\n\r\n<p><strong>Bảo quản:</strong></p>\r\n\r\n<p>Nơi kh&ocirc; r&aacute;o, tho&aacute;ng m&aacute;t</p>\r\n\r\n<p>Tr&aacute;nh &aacute;nh nắng trực tiếp.</p>\r\n\r\n<p><strong>Nồng độ:</strong>&nbsp;Eau De Perfume (EDP)</p>\r\n\r\n<p><strong>Quy c&aacute;ch:</strong>&nbsp;75ml</p>\r\n\r\n<p><strong>Thương hiệu:&nbsp;</strong>Lancome</p>\r\n\r\n<p><strong>Xuất Xứ:&nbsp;</strong>Ph&aacute;p</p>\r\n', 2200000, 2000000, '200000', '1514049834lnm.jpg', '2017-12-23', 'VND');

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `tin_tuc`
--

DROP TABLE IF EXISTS `tin_tuc`;
CREATE TABLE IF NOT EXISTS `tin_tuc` (
  `ma_tin_tuc` int(11) NOT NULL AUTO_INCREMENT,
  `tieu_de` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `tieu_de_url` varchar(200) COLLATE utf8_unicode_ci NOT NULL,
  `tom_tat` varchar(500) COLLATE utf8_unicode_ci NOT NULL,
  `chi_tiet` text COLLATE utf8_unicode_ci NOT NULL,
  `hinh` varchar(100) COLLATE utf8_unicode_ci NOT NULL,
  `tac_gia` varchar(50) COLLATE utf8_unicode_ci NOT NULL,
  `ngay_dang` date NOT NULL,
  `ngay_gui` date NOT NULL,
  `so_luot_xem` int(11) NOT NULL DEFAULT '1',
  PRIMARY KEY (`ma_tin_tuc`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

--
-- Đang đổ dữ liệu cho bảng `tin_tuc`
--

INSERT INTO `tin_tuc` (`ma_tin_tuc`, `tieu_de`, `tieu_de_url`, `tom_tat`, `chi_tiet`, `hinh`, `tac_gia`, `ngay_dang`, `ngay_gui`, `so_luot_xem`) VALUES
(1, 'Nấm kim châm xào thịt bò', 'nam-kim-chi-xao-thit-bo', '24/06/2011 - Nấm trắng, ớt đỏ, cần xanh, thịt bò nâu nhạt... Cái nấm xào của bạn không chỉ ngon, bổ mà còn rất bắt mắt. ', 'Nấm trắng, ớt đỏ, cần xanh, thịt bò nâu nhạt... Cái nấm xào của bạn không chỉ ngon, bổ mà còn rất bắt mắt.\n\nNgoài thành phần chủ yếu là protid như mọi loại nấm khác, nấm kim chi còn có các vitamin B1, B2, C, BB... chứa nhiều kẽm, kali, tốt cho những người mắc bệnh huyết áp; đồng thời có tác dụng phòng chống các bệnh gan mật, tiêu hóa.\n\nNấm kim chi chế biến rất đơn giản, lại có thể phối hợp với nhiều loại thực phẩm như xào với mực tươi, với tôm - thịt, thịt gà, thịt bò... Bạn có thể thử chế biến món thịt bò xào với nấm kim chi, một món ăn rất dễ làm, lại không tốn thời gian.\nKhẩu phần 2 người ăn chỉ cần 200g thịt bò (thịt bò phi lê càng ngon) và khoảng 200g nấm. Thịt bò ướp gia vị, xào chín tái, trút ra Cái. Nấm cắt bỏ gốc, rửa với nước có pha chút muối, để ráo. Phi thơm dầu ăn với tỏi, cho nấm vào xào nhanh tay. Lưu ý: nấm rất mau chín, phải làm nhanh, nấm chín quá sẽ bị nhũn, ăn mất ngon và mất nhiều vitamin. Cho thịt bò vào nấm đảo lại một lần nữa rồi cho ra Cái.\n\nTrang trí thêm cần tây cắt ngắn và vài lát ớt tươi. Dọn dùng nóng. Có thể chấm kèm với nước tương cho thêm đậm đà.\n\n                                                                                                    (Theo Phụ Nữ TP HCM)', '20110624152323nam.jpg', '(Theo Phụ Nữ TP HCM)', '2014-01-04', '0000-00-00', 1),
(2, 'Gà hấp lá sen', 'ga-hap-la-sen', '24/06/2011 - Nấm trắng, ớt đỏ, cần xanh, thịt bò nâu nhạt... Cái nấm xào của bạn không chỉ ngon, bổ mà còn rất bắt mắt. ', 'Nấm trắng, ớt đỏ, cần xanh, thịt bò nâu nhạt... Cái nấm xào của bạn không chỉ ngon, bổ mà còn rất bắt mắt.\r\n\r\nNgoài thành phần chủ yếu là protid như mọi loại nấm khác, nấm kim chi còn có các vitamin B1, B2, C, BB... chứa nhiều kẽm, kali, tốt cho những người mắc bệnh huyết áp; đồng thời có tác dụng phòng chống các bệnh gan mật, tiêu hóa.\r\n\r\nNấm kim chi chế biến rất đơn giản, lại có thể phối hợp với nhiều loại thực phẩm như xào với mực tươi, với tôm - thịt, thịt gà, thịt bò... Bạn có thể thử chế biến món thịt bò xào với nấm kim chi, một món ăn rất dễ làm, lại không tốn thời gian.\r\nKhẩu phần 2 người ăn chỉ cần 200g thịt bò (thịt bò phi lê càng ngon) và khoảng 200g nấm. Thịt bò ướp gia vị, xào chín tái, trút ra Cái. Nấm cắt bỏ gốc, rửa với nước có pha chút muối, để ráo. Phi thơm dầu ăn với tỏi, cho nấm vào xào nhanh tay. Lưu ý: nấm rất mau chín, phải làm nhanh, nấm chín quá sẽ bị nhũn, ăn mất ngon và mất nhiều vitamin. Cho thịt bò vào nấm đảo lại một lần nữa rồi cho ra Cái.\r\n\r\nTrang trí thêm cần tây cắt ngắn và vài lát ớt tươi. Dọn dùng nóng. Có thể chấm kèm với nước tương cho thêm đậm đà.\r\n\r\n                                                                                                    (Theo Phụ Nữ TP HCM)', 'ga_hap_la_sen.jpg', '(Theo Phụ Nữ TP HCM)', '2014-01-04', '2014-01-04', 1),
(3, 'No mắt ở Ngày hội cây trái ngon', 'no-mat-o-ngay-hoi-cay-trai-ngon', '24/06/2011 - 212 sản phẩm đặc trưng của vùng đồng bằng sông Cửu Long tham gia hội thi cây trái ngon tại Bến Tre hôm 7/6. Sầu riêng trĩu quả, khoai lang khổng lồ, trái dứa hình thù kỳ dị... dự đấu xảo (thi) hàng lạ. ', '212 sản phẩm đặc trưng của vùng đồng bằng sông Cửu Long tham gia hội thi cây trái ngon tại Bến Tre hôm 7/6. Sầu riêng trĩu quả, khoai lang khổng lồ, trái dứa hình thù kỳ dị... dự đấu xảo (thi) hàng lạ. \r\n212 sản phẩm đặc trưng của vùng đồng bằng sông Cửu Long tham gia hội thi cây trái ngon tại Bến Tre hôm 7/6. Sầu riêng trĩu quả, khoai lang khổng lồ, trái dứa hình thù kỳ dị... dự đấu xảo (thi) hàng lạ. ', '20110624153825trai-cay-la-7-1.jpg', 'Phương Yến', '2014-01-04', '2014-01-04', 1),
(4, 'Thịt ngâm nước mắm', 'thit-ngam-nuoc-mam', 'Thịt ngâm nước mắm', 'Thịt ngâm nước mắm', 'thit_ngam_nuoc_mam.jpg', 'sass', '2014-01-04', '2014-01-04', 1),
(5, 'Mẹo vặt với hành tây', 'meo-vat-voi-hanh-tay', 'Mẹo vặt với hành tây', 'Mẹo vặt với hành tây', 'meo_vat_voi_hanh_tay.jpg', 'sddd', '2014-01-04', '2014-01-04', 1),
(6, 'Sizzlin\' Steak – Thịt nướng cao cấp trên Cái đá nóng', 'sizzlin-steak-thit-nuong-cao-cap-tren-dia-da-nong', 'Chương trình “Mua bột Takyfood sút ngay Vespa về” của Công ty CP Bột Thực Phẩm Tài Ký diễn ra từ ngày 15/7 đến 12/10/2016, mặc dù mới đi được 1/3 chặng đường nhưng đã có năm người tiêu dùng may mắn trúng 2 giải xe Piaggio Vespa và 03 giải xe Honda Ware Alpha.', 'Để tiếp tục giới thiệu hương vị thịt nướng trên Cái đá nóng đặc trưng của thương hiệu Sizzlin’ Steak đến các tín đồ ẩm thực Việt Nam, sắp tới đây vào ngày 02/09/2016 cửa hàng thứ hai sẽ được khai trương tại khu đô thị Phú Mỹ Hưng, 129 Nguyễn Đức Cảnh, quận 7. Thương hiệu Sizzlin\' Steak ngay khi vừa ra mắt đã chinh phục các thực khách nhờ vào phong cách phục vụ độc đáo và hương vị món ăn đặc trưng.\r\n\r\nSizzlin\' Steak – Thịt nướng cao cấp trên Cái đá nóng - 1\r\n\r\nChương trình khuyến mãi hấp dẫn mừng khai trương cửa hàng Sizzin’ Steak thứ hai\r\n\r\nCó thể nói, việc mở cửa hàng thứ hai không thể thực hiện nếu thiếu đi sự ủng hộ và yêu thích của khách hàng đối với các món ăn ở Sizzlin\' Steak. Chính vì vậy, để tri ân khách hàng đồng thời tưng bừng khai trương của cửa hàng thứ hai, trong tuần đầu tiên (02/09 – 08/09), Sizzlin\' Steak sẽ có chương trình ưu đãi đặc biệt với 03 phần ăn chất lượng nhất, bao gồm: Sizzlin\' Beef Rice (Cơm bò) - Beef Kimchi Rice & Egg (Cơm kim chi bò trứng) - Beefsteak Frites (Bò bít tết) cùng một phần nước ngọt đi kèm với giá cực tốt, lần lượt 99k – 119k – 149k. Ngoài ra, Sizzlin’ Steak còn dành tặng cho 100 vị khách đầu tiên của mỗi ngày trong ba ngày đầu khai trương từ 02/09 - 04/09/2016, nhiều phần voucher và quà tặng hấp dẫn, độc quyền của thương hiệu Sizzlin’ Steak.\r\n\r\nSizzlin\' Steak – Thịt nướng cao cấp trên Cái đá nóng - 2\r\n\r\nBeef Kimchi Rice & Egg (Cơm kim chi bò trứng)\r\n\r\nNgoài các món thiết kế sẵn, khách hàng khi đến Sizzlin\' Steak cũng có thể tự sáng tạo cho mình một phần ăn độc đáo với việc tự do lựa chọn một trong 4 loại thịt (bò, gà, heo, cá hồi), 6 loại sốt và 6 món ăn kèm. Mỗi phần ăn cũng sẽ được tặng kèm thêm một phần cơm để thực khách ngoài việc ngon miệng, còn cảm thấy được no nê và hài lòng về chất lượng cũng như số lượng của món ăn. Giá cả khi so sánh với chất lượng của sản phẩm cũng là một điểm cộng, khi món ăn được thực hiện từ nguyên liệu tươi ngon, hương vị đặc trưng cộng thêm không gian sang trọng và phong cách phục vụ chuyên nghiệp, khách hàng chắc chắn sẽ rất hài lòng về số tiền mình đã bỏ ra.\r\n\r\nSizzlin\' Steak – Thịt nướng cao cấp trên Cái đá nóng - 3\r\n\r\nSizzlin’ Steak là thương hiệu nổi tiếng toàn cầu của Tập đoàn MGI với đặc trưng chuyên phục vụ những món ăn cao cấp trên Cái đá nóng. Trong kế hoạch phát triển mở rộng thương hiệu Sizzlin’ Steak ra toàn thế giới, Việt Nam đã được lựa chọn như một thị trường đầy tiềm năng để phát triển. Và tại đây, công ty L Concepts trở thành đối tác phát triển Sizzlin’ Steak nhờ vào những giá trị cốt lõi phù hợp đối với thương hiệu nổi tiếng toàn cầu này. Đó chính là những giá trị về trách nhiệm, sự tập trung, trung thực và khiêm tốn. Hiện tại, ở Việt Nam, thương hiệu Sizzlin’ Steak hiện đang xây dựng một đội ngũ vững chắc và giàu kinh nghiệm nhằm bảo đảm chất lượng món ăn luôn ở mức tốt nhất và ổn định nhất để luôn đem đến sự hài lòng cho khách hàng. \r\n\r\n', 'thit-nuong-tren-dia-da-nong-1.jpg', 'http://www.24h.com.vn', '2016-09-01', '2016-09-01', 1),
(7, 'Ám áp mùa mưa với Bộ sưu tập ngon khó cưỡng của đầu bếp Tuấn Hải', 'am-ap-mua-mua-voi-bst-ngon-kho-cuong-cua-dau-bep-tuan-hai', 'Gương mặt quen thuộc của chương trình Masterchef Việt Nam, đầu bếp Phạm Tuấn Hải sẽ tiết lộ bí quyết để làm nên 3 món ăn vừa ngon miệng, độc đáo, vừa bổ dưỡng để chị em vào bếp trổ tài chiêu đãi gia đình trong những ngày mưa lạnh.', 'Triết lý sáng tạo của Chef Tuấn Hải\r\n\r\nTrò chuyện về khái niệm “sáng tạo” bên lề các món ăn ngon mang thương hiệu độc đáo của mình, đầu bếp Tuấn Hải chia sẻ: “Với tôi, nghề đầu bếp luôn cần sự sáng tạo nhưng vẫn phải giữ được bản chất của món ăn đó. Cái tài của người đầu bếp là biết kết hợp hài hòa giữa truyền thống với hiện đại, dựa trên nền tảng nguyên liệu có sẵn mà không làm thay đổi vị của món ăn. Ví dụ món Pháp thì cần nhẹ nhàng, tinh tế; món Thái thì luôn giữ vị cay và chua.”\r\n\r\nÁm áp mùa mưa với Bộ sưu tập ngon khó cưỡng của đầu bếp Tuấn Hải - 1\r\n\r\nĐầu bếp Tuấn Hải – vị bếp trưởng đam mê tinh hoa ẩm thực và luôn sáng tạo trong từng món ăn\r\n\r\nLà một đầu bếp dày dạn kinh nghiệm, qua nhiều vai trò như nghiên cứu sản phẩm gia vị, giám khảo Masterchef Việt Nam, chủ nhà hàng…, đầu bếp Tuấn Hải đã đúc kết nhiều bí quyết ẩm thực độc đáo, đặc biệt là cách sử dụng dầu ăn như một loại nguyên liệu thú vị để “biến tấu” nên những món ăn vừa ngon miệng vừa giàu dinh dưỡng. Anh khẳng định, có những món nên dùng dầu thực vật, có những món nhất định phải dùng mỡ động vật mới thơm ngon, béo ngậy.\r\n\r\nQua nhiều thử nghiệm, Tuấn Hải cho biết, dầu ăn cao cấp Ranee chiết xuất từ 100% tinh dầu cá da trơn là lựa chọn ưng ý nhất, làm món ăn thêm đậm vị, màu sắc sáng đẹp, lại dồi dào những thành phần dinh dưỡng tự nhiên như Omega 3-6-9, vitamin E, vitamin A, mang lại nhiều lợi ích sức khỏe. Tuấn Hải chia sẻ thêm, nhờ áp dụng công nghệ hiện đại, nên dầu Ranee không có mùi tanh, giúp anh phối hợp đa dạng vào các món chiên, xào, trộn lẫn nấu súp.\r\n\r\nMón ngon từ Chef Tuấn Hải\r\n\r\nMón 1 – Salad đồng quê\r\n\r\nNguyên liệu: Salad Đà Lạt (400g), salad tím (200g), cải mầm (100g), củ cải đỏ (50g), dấm (50ml), Dầu Ranee (150ml), cà chua bi (50ml), muối & tiêu (vừa nêm).\r\n\r\nCách chế biến:\r\n\r\nPha dầu Ranee với dấm nêm nếm vừa ăn\r\n\r\nLàm sạch các loại rau củ. Cà chua cắt đôi, củ cải đỏ cắt lát\r\n\r\nTrộn đều các rau củ với hỗn hợp dầu Ranee.\r\n\r\nÁm áp mùa mưa với Bộ sưu tập ngon khó cưỡng của đầu bếp Tuấn Hải - 2\r\n\r\nSalad đồng quê với nguyên liệu đa dạng, màu sắc phong phú và vị chua – ngọt tự nhiên, tươi mát\r\n\r\nMón 2 – Bò cuộn phô mai\r\n\r\nNguyên liệu: Bò phi lê (600g), giăm-bông (200g), bột mì (100g), bột chiên xù (200g), trứng (2 quả), dầu Ranee (200ml), phô mai lát (8 lát).\r\n\r\nCách chế biến:\r\n\r\nXếp theo thứ tự: 1 lớp bò lát mỏng, 1 lớp giăm bông – 1 lớp phô mai – 1 lớp giăm bông, 1 lớp bò lát mỏng.\r\n\r\nPhủ bột mì khô lên bò. Sau đó nhúng qua trứng rồi nhúng qua bột chiên xù.\r\n\r\nLàm nóng dầu Ranee rồi chiên bò chín vàng, trang trí và dùng nóng.\r\n\r\nÁm áp mùa mưa với Bộ sưu tập ngon khó cưỡng của đầu bếp Tuấn Hải - 3\r\n\r\nBò cuộn phô mai ngoài giòn trong mềm, kết hợp hài hòa giữa vị béo của phô mai và vị ngọt tự nhiên của thịt bò tươi rất kích thích vị giác\r\n\r\nMón 3 – Gà xào hạt điều\r\n\r\nNguyên liệu: Gà phi lê (400g), hạt điều (80g), ớt xanh Đà Lạt (200g), hành tây (100g), rau mầm (20g), dầu Ranee (50ml), dầu hào (30g).\r\n\r\nCách chế biến:\r\n\r\nGà cắt miếng vừa ăn, ướp xốt dầu hào.\r\n\r\nỚt xanh, hành tây cắt miếng. Hạt điều chiên sơ.\r\n\r\nChảo nóng, cho dầu Ranee vào xào với gà, cho thêm hành tây và ớt xanh.\r\n\r\nKhi gà chín cho thêm hạt điều, trang trí ăn kèm rau mầm.\r\n\r\nÁm áp mùa mưa với Bộ sưu tập ngon khó cưỡng của đầu bếp Tuấn Hải - 4\r\n\r\nThịt gà kết hợp với hạt điều bùi bùi, cùng vị thơm nồng của ớt Đà Lạt sẽ là món khoái khẩu trong những ngày hè\r\n\r\nÁm áp mùa mưa với Bộ sưu tập ngon khó cưỡng của đầu bếp Tuấn Hải - 5\r\n\r\nDầu ăn cao cấp Ranee là sản phẩm của Công ty Cổ phần Dầu cá Châu Á – Tập đoàn Sao Mai, được các chuyên gia hàng đầu Châu Âu tư vấn công nghệ tinh luyện kết hợp cùng đội ngũ chuyên gia Viện Dinh Dưỡng Quốc Gia nghiên cứu, kiểm tra, đánh giá sản phẩm. Ranee được chiết xuất 100% từ tinh dầu cá da trơn, chứa nhiều axit béo không no, Omega 3, 6, 9, EPA, DHA, canxi, vitamin E, vitamin A tự nhiên, thiết yếu cho cơ thể. Sản phẩm sử dụng công nghệ Desmet tách mùi nên không sợ tanh. Dầu ăn cao cấp Ranee là sản phẩm được Viện Dinh Dưỡng Quốc Gia khuyên dùng.\r\n\r\nĐể biết thêm thông tin, vui lòng truy cập http://www.ranee.com.vn hoặc liên hệ hotline: 0673 680 997.', '1471856704-dau-bep-tuan-hai-3', 'vepress.net', '2016-09-01', '2016-09-01', 1),
(8, 'Gà Phủ Phô Mai Cay kích thích vị giác, hút hồn giới trẻ', 'ga-phu-pho-mai-cay-kich-thich-vi-giac-hut-hon-gioi-tre', 'Bắt đầu từ 15/8/2016, Popeyes lại “trình làng\" một loại gà rán hứa hẹn sẽ hút hồn giới trẻ: Gà Phủ Phô Mai Cay', 'Nếu là người mê món gà rán, bạn chắc hẳn đã biết đến thương hiệu gà rán Popeyes xuất phát từ Louisiana, Mỹ, với hương vị đậm đà và thịt gà săn chắc, chất lượng. Chỉ trong 3 năm từ khi xuất hiện tại Việt Nam, Popeyes đã mang lại không biết bao nhiêu “cơn sốt ẩm thực” với các loại gà rán đa dạng mùi vị. Bắt đầu từ 15/8/2016, Popeyes lại “trình làng\" một loại gà rán hứa hẹn sẽ hút hồn giới trẻ: Gà Phủ Phô Mai Cay.\r\n\r\nNhắc tới gà phô mai, nhiều người nghĩ ngay tới món ăn ở các nhà hàng chuyên về thịt gà của Hàn Quốc với gà thường được cắt thành miếng hoặc viên vừa miệng và phô mai có độ sệt và dai vừa đủ. Tuy nhiên, Gà Phủ Phô Mai Cay của Popeyes lại mang một nét đặc trưng rất mới lạ khi kết hợp hình thức gà rán – thức ăn nhanh với sốt phô mai cay phủ lên bề mặt. Do đó, người ăn vừa cảm nhận được độ giòn của gà rán, lại vừa thưởng thức được vị phô mai béo béo, ngậy ngậy, thêm phần cay nồng.\r\n\r\nThịt gà tươi được ướp gia vị tiêu cay, tẩm bột, lăn bột thủ công và chiên giòn, sau đó được phủ với sốt phô mai cay trộn lẫn với mùi tây khô & hành phi giòn rụm.\r\n\r\nNếu muốn có một bữa ăn đầy đủ và tiết kiệm, bạn cũng có thể chọn phần ăn combo 86.000 đồng bao gồm 2 miếng gà phủ phô mai, 1 phần ăn kèm, 1 bánh nướng mật ong, 1 nước (free refill). Đối với Gà Phủ Phô Mai Cay thì phần ăn kèm lý tưởng bạn nên chọn là khoai tây chiên Cajun. Vị đậm đà của khoai tây chiên Cajun tạo thành sự kết hợp hoàn hảo với vị ngậy của Gà Phủ Phô Mai Cay.\r\n\r\nGà Phủ Phô Mai Cay kích thích vị giác, hút hồn giới trẻ - 1\r\n\r\nPhần combo “chuẩn không cần chỉnh” dành cho Gà Phủ Phô Mai Cay: 2 miếng Gà Phủ Phô Mai Cay, khoai tây chiên Cajun, bánh bơ nướng mật ong và nước uống tùy chọn, với giá cực sốc chỉ 86.000 đ\r\n\r\nNgoài việc giới thiệu món gà mới này, Popeyes còn tạo ra những hoạt động tương tác hết sức thú vị tại các cửa hàng của mình để các bạn “tín đồ gà rán Popeyes” có thể thử sản phẩm mới với giá cực sốc. Chỉ cần tới cửa hàng tham dự trò chơi đơn giản \"Say cheese cùng Popeyes\" vào 2 khung giờ 11-13h và 18-20h mỗi thứ 6, bạn sẽ được giảm ngay 50% cho món mới này. (Giảm còn 19.000 đ/ miếng gà Gà Phủ Phô Mai Cay). Thể lệ rất đơn giản, bạn chỉ cần đến cửa hàng Popeyes gần nhất trong khung thời gian trên, và nói lớn từ “cheese” vào microphone đặt sẵn tại cửa hàng trong vòng 5 giây là đã có thể nhận phiếu ưu đãi.\r\n\r\nGà Phủ Phô Mai Cay kích thích vị giác, hút hồn giới trẻ - 2\r\n\r\nPhần ăn Gà Phủ Phô Mai Cay với 2 ưu đãi Combo 99k cho 2 người và 149k cho 3 người\r\n\r\nNgoài ra, nếu đi với bạn bè, người thân, bạn còn có thể lựa chọn phần ăn Group Combo với 2 mức giá: combo cho 2 người 99.000 đ (2 miếng gà phủ phô mai, 2 miếng gà không xương, 2 bánh mật ong, 1 khoai tây nghiền) hoặc combo cho 3 người 149.000 đ (2 miếng gà phủ phô mai, 1 burger tôm, 3 miếng gà không xương, 1 chén sốt Blacked Ranch, 1 bắp cải trộn, 1 khoai tây nghiền, 3 nước có free refill).\r\n\r\nĐối với các bạn bận rộn, không có điều kiện ghé thăm các cửa hàng, hãy gọi ngay hotline giao hàng 19006008 để được phục vụ món gà phủ phô mai cay ngay tại nhà trong thời gian nhanh nhất.', '1471579808-ga-phu-do-1', 'vnpress.net', '2016-09-01', '2016-09-01', 1);

